#!/usr/bin/env node
import inquirer from "inquirer";
import chalk from "chalk";
interface Student {
    id: string;
    name: string;
    enrolledCourses: string[];
    balance: number;
  }
  

  function generateStudentID(): string {
    const randomNumber = Math.floor(Math.random() * 90000) + 10000;
    return randomNumber.toString();
  }
  
  const students: Record<string, Student> = {};
  
  function addStudent(name: string): string {
    const id = generateStudentID();
    const newStudent: Student = {
      id,
      name,
      enrolledCourses: [],
      balance: 100000,
    };
    students[id] = newStudent;
    return id;
  }
  
  function enrollStudent(studentID: string, course: string): void {
    const student = students[studentID];
    if (student) {
      student.enrolledCourses.push(course);
    } else {
      console.log("Student not found.");
    }
  }
  
  function viewBalance(studentID: string): number {
    const student = students[studentID];
    if (student) {
      return student.balance;
    } else {
      console.log("Student not found.");
      return 0;
    }
  }
  
  function payTuition(studentID: string, amount: number): void {
    const student = students[studentID];
    if (student) {
      student.balance -= amount;
    } else {
      console.log("Student not found.");
    }
  }
  console.log("")
  function showStatus(studentID: string) {
    const student = students[studentID];
    if (student) {
      console.log(chalk.yellow("Student ID:", student.id));
      console.log("Name:", student.name);
      console.log("Enrolled Courses:", student.enrolledCourses.join(", "));
      console.log("Balance:", student.balance);
      console.log("")
    } else {
      console.log("Student not found.");
    }
  }
// students
  const studentID1 = addStudent("zaryab ahmed");
  const studentID2 = addStudent("Hafiz azfar maqsood");
  const studentID3 = addStudent("hafiz burhan ahmed");
//   first student
  enrollStudent(studentID1, "web development");
  payTuition(studentID1, 4500);
// second student

enrollStudent(studentID2, "AI");
payTuition(studentID2, 5000);
  
// third student
console.log("")
  
enrollStudent(studentID3, "web development");
  payTuition(studentID1, 4500);

console.log("")


  showStatus(studentID1);
  showStatus(studentID2);
  showStatus(studentID3);

  
